    import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;
     		@GeneratorDelegateClass(componentType="AP", descriptionKey="GeneratorDelegateClass.LKMHiveToOracle_OLHOSCH")   

        public class LKM_Hive_to_Oracle_OLHOSCH extends HiveOlhBaseLKM {

            // class constructor; initialization is done in setBinding method
            public LKM_Hive_to_Oracle_OLHOSCH() {}

            public void setBinding( Binding binding ) {
                super.setBinding( binding )
                // Initialize variables
                useHiveStaging                                      = isOptionTrue( "USE_HIVE_STAGING_TABLE" )
                isDirectLoad                                        = false
            }

            void updateBinding() {
                super.updateBinding()
                // Publish variables into binding to make them available in templates and variableDefText
            }

            /**
             * Create a SqlQuery AST representing inputQuery plus any pushed expressions 
             * of the current node and plus journalization attributes
             * @param inputQuery The upstream AST
             * Copied from LKM_SQL_to_SQL_v2
             */
            protected SqlQuery getTgtSelectQuery(SqlQuery inputQuery) {
                def srcTechno      = physicalNode.getAPNodeSourceExUnit().getLocation().getTechnology()
                def destTechnoName = physicalNode.getExecutionUnit().getLocation().getTechnology().getInternalName()

                List<ISelectItem> newSelectList = new ArrayList<ISelectItem>();
                List<StringExpression> newAliasList = new ArrayList<StringExpression>();

                // Called from a *direct* load LKM?
                if ( physicalNode.isDirectLoad() ) {
                    def targetNode = physicalNode.getParentNode()
                    List<MapAttribute> attrs = targetNode.getLogicalComponent().getAttributes();
                    for (MapAttribute attr : attrs) {
                        // for each target attribute, either create an array expression node that represents a simple reference to the attribute...
                        if (attr.isMapped()) {                            
                            //TODO: Adding object delimiters is not a task for KMs! This should be 
                            //handled transparently inside the codegen, by moving the respective task
                            //to source side. Which currently fails with NPE.
                            def aliasName = attr.getName()
                            // Add double quotes when needed
                            // Standard double-quoting rules for non-HBase targets
                            // Force double-quoting for HBase targets, as we must ensure that alias names
                            // do not get upper or lower cased by source database
            	            aliasName = MapAttribute.getSQLAccessName(srcTechno, aliasName, false )

                            newSelectList.add(new ArrayExpression(targetNode, attr, null, inputQuery));
                            newAliasList.add(new StringExpression( aliasName )); 
                        } else {
                            // for direct Load KMs using ext tabs, we have to include unmapped columns. (The target table will be an ext tab.)
                            if ( isExtTabRequired ) {
                            // or an empty string expression for unmapped columns
                            def unmappedValue = "null"
                            newSelectList.add(new StringExpression(unmappedValue));
                            newAliasList.add(new StringExpression(attr.getName(),attr)); 
                            }
                        }
                    }
                } else {
                    // Called from a standard LKM
                    List<MapPhysicalColumn> cols = physicalNode.getPhysicalColumns();                	
                    for (MapPhysicalColumn col : cols) {
                        // for each phys column (attribute), create an array expression node that represents a simple reference to the attribute
                        newSelectList.add(new ArrayExpression(col, inputQuery));
                        newAliasList.add(new StringExpression(col.getName(),col)); 
                    }
                }

                SqlQuery result = inputQuery.clone();
                result.setSelectList(newSelectList);
                result.setAliasList(newAliasList);
                return result;
            }


            @ASTProducerMethod(processingType=ProcessingType.SOURCE, producedASTType=SqlQuery.class, styleKey="ProcessingStyleKey.Hive.SELECT")
            public AbstractSyntaxTree SELECT() {
              return LKM_SQL_to_SQL_v2__SELECT( null )
            }


        	@ASTProducerMethod(processingType=ProcessingType.TARGET, producedASTType=NullTarget.class, styleKey="ProcessingStyleKey.Oracle.INSERT")      
            public AbstractSyntaxTree INSERT( SqlQuery input ) {
                def upNode = physicalNode.getUpstreamConnectedNodes()[0]
                setHiveSourceTableOrQuery( getTgtSelectQuery( input ), upNode )

                // We do not need any *target* Hive connection
                // NB: Hive *source* connection is getting configured in task below
                prepareHiveSession = false

                // Create DDL for staging table if needed
                if ( !isOutputModeOSCH )
                    defineSqlStagingTable()

                // Create DDL for staging table
                def stagingTable = getStagingTable()
                stgTableAST = new HiveCreateTableStatement( "abc", "def", stagingTable, getHiveTechnology(), ffFinder )
                def stgTableColumnList = physicalNode.getPhysicalColumns().collect { physCol -> return Column.getColumn( stagingTable, physCol) }                
                stgTableAST.setColumns( stgTableColumnList )                

                // Publish variables into binding to make them available in templates and variableDefText
                updateBinding()
                return new NullTarget()
            }

        } // End of class definition
