    import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;
@GeneratorDelegateClass(componentType="AP", descriptionKey="GeneratorDelegateClass.LKMHiveToFile")

        public class LKM_Hive_to_File_Direct extends HiveBaseKM {
            protected SqlQuery sourceQuery
            protected String sourceQueryLSchema
            protected String fieldSeparator, rowSeparator, sourceDir
            protected String osStdOutFileName, osErrFileName

            // class constructor; initialization is done in setBinding method
            public LKM_Hive_to_File_Direct() {}

            // Declare KM level vars        
            protected boolean option_FILE_IS_LOCAL
            protected String option_STORED_AS

            public void setBinding( Binding binding ) {
                super.setBinding( binding )
                // Initialize variables
                option_FILE_IS_LOCAL = isOptionTrue( "FILE_IS_LOCAL" )
                option_STORED_AS     = getOption("STORED_AS")

                def targetNode = physicalNode.getParentNode()
                OdiDataStore targetFile = targetNode.getLogicalComponent().getBoundObject()
                def fileDesc = targetFile.getFileDescriptor()
                if (!SnpsStringTools.replaceNVL(fileDesc.getFieldSeparator()).trim().equals(""))
                    fieldSeparator = SnpsStringTools.replaceNVL(SnpsStringTools.escapeUnicode(SnpsStringTools.hexStrToStr(fileDesc.getFieldSeparator()), true))
                if (!SnpsStringTools.replaceNVL(fileDesc.getRowSeparator()).trim().equals(""))
                    rowSeparator = SnpsStringTools.replaceNVL(SnpsStringTools.escapeUnicode(SnpsStringTools.hexStrToStr(fileDesc.getRowSeparator()), true))

                //Validate File format
                if ( fileDesc.getFormat() != oracle.odi.domain.model.OdiDataStore$FileDescriptor$Format.DELIMITED ) {
                    throw new OdiKMException(
                    "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: Only delimited target files supported.", 
                    null );
                }

                def physicalSchema = '''<?=odiRef.getInfo("DEST_SCHEMA")?>'''
                def resourceName = targetFile.getResourceName();
                sourceDir = physicalSchema + "/" + resourceName;
                osStdOutFileName = physicalSchema + "/" + "odi.out";
                osErrFileName =    physicalSchema + "/" + "odi.err";
            }

            void updateBinding() {
                super.updateBinding()

                // Publish variables into binding to make them available in templates and variableDefText
                mBinding.setVariable( "option_FILE_IS_LOCAL", option_FILE_IS_LOCAL )
                mBinding.setVariable( "option_STORED_AS", option_STORED_AS )
                mBinding.setVariable( "fieldSeparator", fieldSeparator )
                mBinding.setVariable( "rowSeparator",   rowSeparator )
                mBinding.setVariable( "sourceDir",   sourceDir )
                mBinding.setVariable( "osStdOutFileName",   osStdOutFileName )
                mBinding.setVariable( "osErrFileName",   osErrFileName )
            }

            /**
             * Create a SqlQuery AST representing inputQuery plus any pushed expressions 
             * of the current node and plus journalization attributes
             * @param inputQuery The upstream AST
             * Copied from LKM_SQL_to_SQL_v2
             */
            protected SqlQuery getTgtSelectQuery(SqlQuery inputQuery) {
                def srcTechno      = physicalNode.getAPNodeSourceExUnit().getLocation().getTechnology()
                def destTechnoName = physicalNode.getExecutionUnit().getLocation().getTechnology().getInternalName()

                List<ISelectItem> newSelectList = new ArrayList<ISelectItem>();
                List<StringExpression> newAliasList = new ArrayList<StringExpression>();

                // Called from a *direct* load LKM?
                if ( physicalNode.isDirectLoad() ) {
                    def targetNode = physicalNode.getParentNode()
                    List<MapAttribute> attrs = targetNode.getLogicalComponent().getAttributes();
                    for (MapAttribute attr : attrs) {
                        // for each target attribute, either create an array expression node that represents a simple reference to the attribute...
                        if (attr.isMapped()) {                            
                            //TODO: Adding object delimiters is not a task for KMs! This should be 
                            //handled transparently inside the codegen, by moving the respective task
                            //to source side. Which currently fails with NPE.
                            def aliasName = attr.getName()
                            // Add double quotes when needed
                            // Standard double-quoting rules for non-HBase targets
                            // Force double-quoting for HBase targets, as we must ensure that alias names
                            // do not get upper or lower cased by source database
            	            aliasName = MapAttribute.getSQLAccessName(srcTechno, aliasName, false )

                            newSelectList.add(new ArrayExpression(targetNode, attr, null, inputQuery));
                            newAliasList.add(new StringExpression( aliasName )); 
                        } else {
                            // or an empty string expression for unmapped columns
                            def unmappedValue = "''"
                            // for file targets, we need to include even unmapped columns
                            // for HBase we must not include unmapped columns
                            newSelectList.add(new StringExpression(unmappedValue));
                            newAliasList.add(new StringExpression(attr.getName(),attr)); 
                        }
                    }
                } else {
                    // Called from a standard LKM
                    List<MapPhysicalColumn> cols = physicalNode.getPhysicalColumns();                	
                    for (MapPhysicalColumn col : cols) {
                        // for each phys column (attribute), create an array expression node that represents a simple reference to the attribute
                        newSelectList.add(new ArrayExpression(col, inputQuery));
                        newAliasList.add(new StringExpression(col.getName(),col)); 
                    }
                }

                SqlQuery result = inputQuery.clone();
                result.setSelectList(newSelectList);
                result.setAliasList(newAliasList);
                return result;
            }

            void addHiveSourceTask( String kmName, String taskName, String templateText, int order, String lineType ) {
                def aAST =  new CustomAST( kmName, templateText )                
                aAST.setIsPushFromSource(true)
                aAST.setOrder( order )
                aAST.setSourceTechnology( getHiveTechnology() )

                // set source location to the Hive source node
                aAST.setSourceLocation( physicalNode.getChildNodes()[0].getExecutionUnit().getLocation() )
                // set target location to the AP node (file)
                aAST.setTargetLocation( physicalNode.getExecutionUnit().getLocation() )

                def taskContainer = this.getTaskContainer()
                def targetName = physicalNode.getName()
                def aTask = taskContainer.convertASTToTask(aAST, targetName, taskName, physicalNode.getExecutionUnit())             
                aTask.getLine().setLineType(IProcedureLine.LineType.valueOf( lineType ))
                taskContainer.addTask(aTask)
            }

            void addHiveUnloadTask( SqlQuery sourceQuery ) {
                // Add task for Hive unload         
                // TODO: We should use the template here. But this causes an NPE.
                // """{# INCLUDE = 'LkmHiveToFile_UnloadHiveData' #}"""                                
                addHiveSourceTask( "LKM Hive to File Direct", "Unload Hive data", 
                    """INSERT OVERWRITE ${option_FILE_IS_LOCAL?"LOCAL":""} DIRECTORY '${sourceDir}' \n"""+
                    """ROW FORMAT DELIMITED FIELDS \n"""+
                    """TERMINATED BY '${fieldSeparator}' \n"""+
                    """LINES TERMINATED BY '${rowSeparator}' \n"""+
                    """STORED AS ${option_STORED_AS} \n"""+
                    """${sourceQuery.getText()}""", 250, "EX_UNIT_MAIN" )
            }

            @ASTProducerMethod(processingType=ProcessingType.TARGET, producedASTType=HiveFileInsert.class, styleKey="ProcessingStyleKey.File.INSERT")
            public AbstractSyntaxTree INSERT( SqlQuery input ) {
                sourceQuery = getTgtSelectQuery( input )
                //addHiveUnloadTask( sourceQuery )

                HiveFileInsert result = new HiveFileInsert()
                prepareHiveSession = false
                // Publish variables into binding to make them available in templates and variableDefText
                updateBinding()
                //return new NullTarget()
                result.setSourceDir(mBinding.getVariable("sourceDir"))  // The value of sourceDir was set in updateBinding().
                result.setHiveUnloadQuery(sourceQuery)
                result.setFieldSeparator(mBinding.getVariable("fieldSeparator"))
                result.setRowSeparator(mBinding.getVariable("rowSeparator"))
                return result
            } 

        } // End of class definition

