Object.Field:

    import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
    import oracle.odi.mapping.generation.AbstractSyntaxTree.SubstitutionAPIMethod;
    import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;
    @GeneratorDelegateClass(componentType="AP", descriptionKey="GeneratorDelegateClass.HiveSqoopImportBaseLKM")    
    public class HiveSqoopImportBaseLKM extends HiveSqoopBaseLKM {    	
        // class constructor; initialization is done in setBinding method
        public HiveSqoopImportBaseLKM() {}        
        // Declare KM level vars
        protected String option_SQOOP_PARALLELISM
        protected String option_SPLIT_BY
        protected String option_BOUNDARY_QUERY    
        protected String sqoopQueryString  
        protected boolean isToHive, isToFile, isToHBase, isFromOracle, isFromIbmDb2Udb, isFromMsSQL, isFromTeradata, isFromMySQL, isFromNetezza
        protected srcTechno, destTechno  

        public void setBinding( Binding binding ) {
            super.setBinding( binding )
            // Initialize variables
            option_SQOOP_PARALLELISM   = getOption(    "SQOOP_PARALLELISM" )
            option_SPLIT_BY       = getOption( "SPLIT_BY" )
            option_SPLIT_BY       = option_SPLIT_BY==null?"":option_SPLIT_BY.trim()
            option_BOUNDARY_QUERY = getOption( "BOUNDARY_QUERY" )
            option_BOUNDARY_QUERY = option_BOUNDARY_QUERY==null?"":option_BOUNDARY_QUERY.trim()

            srcTechno          = physicalNode.getAPNodeSourceExUnit().getLocation().getTechnology()
            destTechno         = physicalNode.getExecutionUnit().getLocation().getTechnology()

            def srcTechnoName  = srcTechno.getInternalName()            
            def destTechnoName = destTechno.getInternalName()            
            isToFile           = destTechnoName.equalsIgnoreCase("FILE")
            isToHive           = destTechnoName.equalsIgnoreCase("HIVE")
            isToHBase          = destTechnoName.equalsIgnoreCase("HBASE")            
            isFromOracle       = srcTechnoName.equalsIgnoreCase ("ORACLE")
            isFromIbmDb2Udb    = srcTechnoName.equalsIgnoreCase ("IBM_DB2_UDB")
            isFromMsSQL        = srcTechnoName.equalsIgnoreCase ("MICROSOFT_SQL_SERVER")
            isFromTeradata     = srcTechnoName.equalsIgnoreCase ("TERADATA")
            isFromMySQL        = srcTechnoName.equalsIgnoreCase ("MYSQL")
            isFromNetezza      = srcTechnoName.equalsIgnoreCase ("NETEZZA")
        }

        public void setSqoopQuery ( SqlQuery input ) {  
            sqoopQueryString = input.getText()

            // gen'ed query must not contain any empty lines and any line must be terminated by a backslash
            sqoopQueryString = sqoopQueryString.replaceAll( "\n", Matcher.quoteReplacement(" \\\n") )
            sqoopQueryString += " \\"

            /* Not needed, as C$ table column names are under control of the user
               May be reincluded later if we want to support any attribute names on APs
            // Currently we need to double-quote CX_COL_NAMES in some cases:
            // Add double-quotes for HBase target
            def dq = '"'
            def odq = ''

            if ( isToHBase ) {
                if ( isFromMySQL ) {
                    // For MySQL we must use ` character
                    odq = ""+(char)96;
                } else {
                    odq = dq;
                }
            }
            */

            // If SPLIT_BY is defined, we have to surround the query by an outer SELECT for certain technologies. 
            // Otherwise target column names used in SPLIT_BY expression are not resolved on some databases 
            // (Oracle does not allow column aliases in SELECT list to be used in where conditions.
            // extra select is needed for Oracle, DB2, MSSQL, not needed for Teradata and sometimes needed for MySQL
            def addOuterSelect = false
            if (option_SPLIT_BY.length() > 0) {
                addOuterSelect = true
                if (isFromTeradata)  addOuterSelect = false
                if (isFromMySQL) {
                    addOuterSelect = false
                    if (isToFile)  addOuterSelect = true
                }
            }
            // Probably for fetching column metadata SQOOP (at least on Oracle) always executes a query with
            // <SQL query> where (1=0) during SQOOP's java generation phase. This query requires a outer select
            // e.g. when there is Filter, Aggregate, Split or other component, whose SQL will fail when appended
            // by "where (1=0)". For simplicity we adding an outer select clause always:
            addOuterSelect = true

            if ( addOuterSelect ) {
                def tableAliasSeparator = (srcTechno.getTableAliasSeparator()==null)?"":srcTechno.getTableAliasSeparator()
                sqoopQueryString = "SELECT * FROM ( \\\n" + sqoopQueryString + "\n) " + tableAliasSeparator + " ODI_SQOOP_QUERY \\\n\\"
            }

        }

        void updateBinding() {
            super.updateBinding()
            // Publish variables into binding to make them available in templates and variableDefText

            def srcTechnoName        = srcTechno.getInternalName()
            def destTechnoName        = destTechno.getInternalName()

            def stageTableName  = generatorContext.getAPNodeCollTableAccessName( physicalNode )
                                  //physicalNode.getStageTableName()     
            // Get COLL_SHORT_NAME                                                           
            def teradataStageTableName = '<?=odiRef.getObjectShortName("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '", "W")?>'
            def teradataQualifiedStagingTable = '<?=odiRef.getObjectName("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '", "W")?>'
            def teradataQualifiedStagingView  = '<?=odiRef.getObjectName("L", "' + "V%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '", "W")?>'
            mBinding.setVariable( "teradataQualifiedStagingTable", teradataQualifiedStagingTable ) 
            mBinding.setVariable( "teradataQualifiedStagingView",  teradataQualifiedStagingView )     

            def useGeneric      = option_USE_GENERIC_JDBC_CONNECTOR
            def isFromTeradataToHive = ( isFromTeradata && isToHive )

            mBinding.setVariable( "useGeneric",      useGeneric )             
            mBinding.setVariable( "isToFile",        isToFile ) 
            mBinding.setVariable( "isToHive",        isToHive ) 
            mBinding.setVariable( "isToHBase",       isToHBase ) 
            mBinding.setVariable( "isFromOracle",    isFromOracle ) 
            mBinding.setVariable( "isFromTeradata",  isFromTeradata ) 
            mBinding.setVariable( "isFromMsSQL",     isFromMsSQL ) 
            mBinding.setVariable( "isFromMySQL",     isFromMySQL ) 
            mBinding.setVariable( "isFromIbmDb2Udb", isFromIbmDb2Udb ) 
            mBinding.setVariable( "isFromNetezza",   isFromNetezza ) 
            mBinding.setVariable( "isFromTeradataToHive", isFromTeradataToHive ) 

            mBinding.setVariable( "srcJavaUrl",    '''<?=odiRef.getInfo("SRC_JAVA_URL")?>''' )
            mBinding.setVariable( "srcJavaDriver", '''<?=odiRef.getInfo("SRC_JAVA_DRIVER")?>''' )
            mBinding.setVariable( "srcUserName",   '''<?=odiRef.getInfo("SRC_USER_NAME")?>''' )
            mBinding.setVariable( "srcPassword", '''<?=odiRef.getInfo("SRC_PASS")?>''' )            

            mBinding.setVariable( "option_SQOOP_PARALLELISM",                     option_SQOOP_PARALLELISM )
            mBinding.setVariable( "option_SPLIT_BY",       option_SPLIT_BY ) 
            mBinding.setVariable( "option_BOUNDARY_QUERY", option_BOUNDARY_QUERY ) 

            mBinding.setVariable( "tempPath", getQuotedString( getOption( "TEMP_DIR" ) ) ) 
            if ( isToFile ) {
                mBinding.setVariable( "targetDir", '''<?=odiRef.getQuotedString( odiRef.getInfo( "DEST_SCHEMA" ))?>''' )
            } else {         
                mBinding.setVariable( "targetDir", getQuotedString( getOption( "MAPRED_OUTPUT_BASE_DIR" ) + "/" + stageTableName ) )
            }                        
            mBinding.setVariable( "srcSchemaName",     '''<?=odiRef.getInfo("SRC_SCHEMA")?>''' ) 
            mBinding.setVariable( "srcWorkSchemaName", '''<?=odiRef.getInfo("SRC_WORK_SCHEMA")?>''' ) 
            mBinding.setVariable( "destSchemaName",    '''<?=odiRef.getInfo("DEST_SCHEMA")?>''' ) 
            mBinding.setVariable( "teradataStageTableName", teradataStageTableName ) 

            if ( isToHive ) {
                  mBinding.setVariable( "hiveTableName",                            getQuotedString(stageTableName) ) 
                mBinding.setVariable( "hiveMetastoreUris",                        '''<?=odiRef.getFlexFieldValue( odiRef.getInfo("DEST_CONNECT_GUID"), "1400", "HIVE_METASTORE_URIS" )?>''' )
                mBinding.setVariable( "hiveMetastoreUrisQuoted",                  '''<?=odiRef.getQuotedString( odiRef.getFlexFieldValue( odiRef.getInfo("DEST_CONNECT_GUID"), "1400", "HIVE_METASTORE_URIS" ) )?>''' )
            }

            if ( option_SPLIT_BY.length() > 0 ) {
                //def splitByCxColName = option_SPLIT_BY

                def splitByCxColName
                // Called from a direct-load KM?
                if (physicalNode.isDirectLoad()) {
                      def attr = physicalNode.getParentNode().getLogicalComponent().findOutputAttribute( option_SPLIT_BY )
                      splitByCxColName = attr?.getName()
                } else {
                    // No, call is from standard LKM
                      def col = physicalNode.findPhysicalColumn( option_SPLIT_BY )
                      splitByCxColName = col?.getName()
                }
                  if ( splitByCxColName == null)
                      throw new OdiKMException( "Cannot find attribute ${option_SPLIT_BY} specified on KM OPTION SPLIT_BY." )                        
                  // Add double quotes when needed
                  // Standard double-quoting rules for non-HBase targets
                  // Force double-quoting for HBase targets, as we must ensure that alias names
                  // do not get upper or lower cased by source database
	              splitByCxColName = MapAttribute.getSQLAccessName(srcTechno, splitByCxColName, isToHBase)

                // Is the split-by column double-quoted?
              if ( splitByCxColName.startsWith( '"' ) ) {
                    // Sqoop bug: for passing a double-quoted column name like "key", this expression must be used: '""key""'
                    //if ( isFromMySQL ) {
                    //    splitByCxColName = '"' + splitByCxColName + '"'
                    //} else {
                    //    splitByCxColName = '""' + splitByCxColName + '""'
                    //}
                    splitByCxColName = '"' + splitByCxColName + '"'
                }
                splitByCxColName = "'" + splitByCxColName + "'"
                mBinding.setVariable( "splitByCxColName", splitByCxColName )
            } else {
                mBinding.setVariable( "splitByCxColName", "" )
            }

            // Validate source technology
            if ( !( isFromOracle || isFromTeradata || isFromMsSQL || isFromMySQL || isFromIbmDb2Udb || isFromNetezza || useGeneric ) ) {
                throw new OdiKMException(
                    "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: This KM does not have specific connector support for source technology {0}. Please consider using USE_GENERIC_JDBC_CONNECTOR = true.", 
                    [ srcTechnoName ] as String[] )
            }

            // Validate target technology
            if (!(isToFile || isToHive || isToHBase)) {
                throw new OdiKMException(
                    "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: Target technology {0} is not supported.", 
                    [ destTechnoName ] as String[] )
            }

            // Validate Teradata Connector limitations (only File output is supported)
            if ((isToHive||isToHBase) && isFromTeradata && !useGeneric) { 
                throw new OdiKMException(
                    "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: Teradata Connector does not support transfer to {0}. Please consider using the generic Sqoop JDBC connector: USE_GENERIC_JDBC_CONNECTOR=1.", 
                    [ destTechnoName ] as String[] )
            }

            // Check that SPLIT_BY is defined when SQOOP_PARALLELISM > 1
            if ( !option_SQOOP_PARALLELISM.equals("1") && (option_SPLIT_BY.equals("")) ) {
                throw new OdiKMException(
                    "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: SQOOP_PARALLELISM is set to {0}. Parallelism of more than 1, requires SPLIT_BY to be set.", 
                    [ option_SQOOP_PARALLELISM ] as String[] );
            }

            // Check that SPLIT_BY is defined when BOUNDARY_QUERY is set
            if ( option_BOUNDARY_QUERY.length()>0 && option_SPLIT_BY.length()==0 ) {
                throw new OdiKMException(
                    "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: BOUNDARY_QUERY is set ({0}). This value is used only, if KM option SPLIT_BY is set. Either remove value from BOUNDARY_QUERY or set SPLIT_BY.", 
                    [ option_BOUNDARY_QUERY ] as String[] );
            }

            mBinding.setVariable( "sqoopQueryString",      sqoopQueryString )         	
        }
    } // End of class definition
