public class OracleErpCloudExtractBaseKM extends GroovyComponentGeneratorDelegateBase {

  protected IMapComponent component;
  protected MapConnectorPoint connector;
  protected MapPhysicalNode physicalNode;
  protected String collTableName;
  protected List<AbstractSyntaxTree> upstreamASTList;
  protected GeneratorContext generatorContext;
  protected ComponentKM componentKM;
  protected String subtype;
  protected String httpUser;
  protected String httpPassword;
  protected String srcWorkSchema;
  protected String srcSchema;
  protected String submitReportResponseFile;
  protected String getReportHistoryInfoResponseFile;
  protected String getScheduledReportOutputInfoResponseFile;
  protected String getDocumentDataResponseFile;
  protected String reportRequestWsdl;
  protected String scheduleRequestWsdl;
  protected String outputFile;
  protected OdiDataStore erpDS;
  protected String BIPReportLocation;
  protected String BIPReportParameters;

  private Binding mBinding = null;

        @Override
  public void setBinding(Binding binding) {
    super.setBinding(binding);
    mBinding = binding;
    Map<String,Object> variables = binding.getVariables();
    if (variables.containsKey("component")) {
      component =  (IMapComponent) mBinding.getVariable("component");
    }
    if (variables.containsKey("connector")) {
      connector =  (MapConnectorPoint) mBinding.getVariable("connector");
    }
    if (variables.containsKey("physicalNode")) {
      physicalNode =  (MapPhysicalNode) mBinding.getVariable("physicalNode");
    }
    if (variables.containsKey("collTableName")) {
      collTableName =  (String) mBinding.getVariable("collTableName");
    }
    if (variables.containsKey("upstreamASTList")) {
      upstreamASTList =  (List<AbstractSyntaxTree>) mBinding.getVariable("upstreamASTList");
    }
    if (variables.containsKey("generatorContext")) {
      generatorContext =  (GeneratorContext) mBinding.getVariable("generatorContext");
    }
    if (variables.containsKey("componentKM")) {
      componentKM =  (ComponentKM) mBinding.getVariable("componentKM");
    }
    if (variables.containsKey("subtype")) {
      subtype =  (String) mBinding.getVariable("subtype");
    }
    try {
      List<MapPhysicalNode> parentNodes = physicalNode.getUpstreamConnectedNodes();
      if (parentNodes.size() != 1)
        throw new RuntimeException();

      while (!parentNodes.get(0).isSourceNode()){
        parentNodes = parentNodes.get(0).getUpstreamConnectedNodes();
      }
      MapPhysicalNode parentNode = parentNodes.get(0);
      srcSchema = parentNode.getExecutionUnit().getLocation().getPhysicalSchema().getSchemaName();
      srcWorkSchema = parentNode.getExecutionUnit().getLocation().getPhysicalSchema().getWorkSchemaName();
      mBinding.setVariable("srcWorkSchema", srcWorkSchema);
      submitReportResponseFile = srcWorkSchema + "/submitReportResponse.xml";
      mBinding.setVariable("submitReportResponseFile", submitReportResponseFile);

      getReportHistoryInfoResponseFile = srcWorkSchema + "/getReportHistoryInfoResponse.xml";
      mBinding.setVariable("getReportHistoryInfoResponseFile", getReportHistoryInfoResponseFile);

      getScheduledReportOutputInfoResponseFile = srcWorkSchema + "/getScheduledReportOutputInfoResponse.xml";
      mBinding.setVariable("getScheduledReportOutputInfoResponseFile", getScheduledReportOutputInfoResponseFile);

      getDocumentDataResponseFile = srcWorkSchema + "/getDocumentDataResponse.xml";
      mBinding.setVariable("getDocumentDataResponseFile", getDocumentDataResponseFile);

      String erpWsdl = parentNode.getExecutionUnit().getLocation().getPhysicalSchema().getDataServer().getConnectionInfo().getJdbcUrl();
      int indexOfPub = erpWsdl.indexOf(".com/");
      reportRequestWsdl = erpWsdl.substring(0, indexOfPub+5) + "xmlpserver/services/PublicReportWSSService?WSDL";
      mBinding.setVariable("reportRequestWsdl", reportRequestWsdl);
      scheduleRequestWsdl = erpWsdl.substring(0, indexOfPub+5) + "xmlpserver/services/ScheduleReportWSSService?WSDL";
      mBinding.setVariable("scheduleRequestWsdl", scheduleRequestWsdl);

      httpUser = parentNode.getExecutionUnit().getLocation().getPhysicalSchema().getDataServer().getUsername();
      mBinding.setVariable("httpUser", httpUser);
      httpPassword = parentNode.getExecutionUnit().getLocation().getPhysicalSchema().getDataServer().getPassword().toString();
      mBinding.setVariable("httpPassword", httpPassword);

      erpDS = (OdiDataStore)parentNode.getLogicalComponent().getBoundObject();
      StringBuffer paramBuffer = new StringBuffer();
      String tableProp = erpDS.getTableProperties();
      tableProp = tableProp.substring(2, tableProp.length()-2);
      //System.out.println("@@@@@@@@@tableProp = " + tableProp);
      int indexOfComma = tableProp.indexOf("\",\"");
      while (indexOfComma > 0) {
        String prop = tableProp.substring(0, indexOfComma);
        int indexOfEqual = prop.indexOf("\"=\"");
	String propName = prop.substring(0, indexOfEqual);
	String propValue = prop.substring(indexOfEqual+3);

	if (propName.equals("BIPReportLocation")) {
          BIPReportLocation = propValue;
        } else {
          paramBuffer.append("<" + propName + ">" + propValue + "</" + propName + ">\\n");
        }

	tableProp = tableProp.substring(indexOfComma+3);
	indexOfComma = tableProp.indexOf("\",\"");
      }
      int indexOfEqual = tableProp.indexOf("\"=\"");
      String propName = tableProp.substring(0, indexOfEqual);
      String propValue = tableProp.substring(indexOfEqual+3);
      if (propName.equals("BIPReportLocation")) 
        BIPReportLocation = propValue;
      else
	paramBuffer.append("<" + propName + ">" + propValue + "</" + propName + ">\\n");

      if (BIPReportLocation == null || BIPReportLocation.trim().equals(""))
	throw new RuntimeException("BIPReportLocation is not defined.");
      BIPReportParameters = paramBuffer.toString();
      mBinding.setVariable("BIPReportLocation", BIPReportLocation);
      mBinding.setVariable("BIPReportParameters", BIPReportParameters);

    } catch (Exception ex) {
      throw new RuntimeException(ex);
    }

  }
} // End of LKM class


import java.io.BufferedReader;
import java.io.FileReader;
import java.io.File;
import java.io.BufferedInputStream;
import java.io.BufferedOutputStream;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.InputStreamReader;
import java.io.IOException;
import java.util.Base64;

inFile = new File("$[getDocumentDataResponseFile]");
reader = new BufferedReader( new InputStreamReader( new FileInputStream(inFile), "UTF8"));
xmlContentBuf = new StringBuffer();
while ((str = reader.readLine()) != null) {
  xmlContentBuf.append(str);
}
reader.close();

xmlContent = xmlContentBuf.toString();
first = xmlContent.indexOf("getDocumentData xmlns");
leftXmlContent = xmlContent.substring(first+86);
last = leftXmlContent.indexOf("getDocumentData");
encryptedData = leftXmlContent.substring(0, last-6);
bytes = encryptedData.getBytes("UTF-8");

decodedBytes = Base64.getDecoder().decode(bytes);

File outFile = new File("$[outputFile]");
BufferedOutputStream writer = new BufferedOutputStream(new FileOutputStream(outFile));
writer.write(decodedBytes); 
writer.flush();
writer.close();
