Object.Field:
	import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
	import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;

	@GeneratorDelegateClass(componentType=AggregateComponent.COMPONENT_TYPE_NAME, descriptionKey="GeneratorDelegateClass.SparkPivotCompKM")    
    public class XKM_Spark_Pivot extends SparkKM {

	 	@ASTProducerMethod(processingType=ProcessingType.SOURCE,producedASTType=SparkScript.class, styleKey="ProcessingStyleKey.SparkPivot.SELECT")       
        SparkScript SELECT(SparkScript inputScript) {

            supportDataFrames('XKM Spark Pivot', false);

            List<SparkScript> inputScripts = new ArrayList<SparkScript>(1);
            inputScripts.add(inputScript);

            SparkPivotCmd pivotCmd = new SparkPivotCmd(physicalNode.getTemplateUtils(), physicalNode);

            if (pivotCmd.isStreamingMode()) {
                throw new OdiKMException("ERROR : XKM Spark Pivot only supports Spark batch mode. Streaming mode is not implemented.");
            }

            SparkScript pivotScript = new SparkScript(inputScript.getTemplateUtils(), inputScripts);
            pivotScript.addChild(pivotCmd);

            String aggFunction = component.getAggregateFunction();
            if(aggFunction != null && aggFunction.length() > 0) {
                pivotCmd.setSubGrpAggrFunc(aggFunction);
            }

            List<String> groupByAliases = new ArrayList<String>();
            List<oracle.odi.domain.mapping.expression.MapExpression> groupByExprs = new ArrayList<oracle.odi.domain.mapping.expression.MapExpression>();
            List<oracle.odi.domain.mapping.expression.MapExpression> subGrpExprs = new ArrayList<oracle.odi.domain.mapping.expression.MapExpression>();
            List<String> subGroupAliases = new ArrayList<String>();
            List<String> subGroupValues = new ArrayList<String>();

            List<MapAttribute> attributes = component.getAttributes();

            oracle.odi.domain.mapping.expression.MapExpression rowLocatorExpr = component.getRowLocator();
            pivotCmd.setSubGrpMatchExpr(rowLocatorExpr);

            for (MapAttribute attribute : attributes) {

                oracle.odi.domain.mapping.expression.MapExpression expression = attribute.getExpression();

                if(expression != null) {

                    String pivotValue = attribute.getPivotMatchingRow();
                    String alias = attribute.getName();
                    if (pivotValue == null || pivotValue.length()== 0) {
                        groupByAliases.add(alias);                        
                        groupByExprs.add(expression);
                    }
                    else {
                        subGroupAliases.add(alias);
                        subGrpExprs.add(attribute.getExpression());
                        subGroupValues.add(pivotValue);
                    }
                }
            }

            pivotCmd.setGroupByExprs(groupByExprs);
            pivotCmd.setGroupByAliases(groupByAliases);

            pivotCmd.setSubGrpExprs(subGrpExprs);
            pivotCmd.setSubGrpValues(subGroupValues);
            pivotCmd.setSubGrpAliases(subGroupAliases);

            List<Integer> groupByIndexes = new ArrayList<Integer>(groupByAliases.size());
            for(int i=0; i<groupByAliases.size(); i++) {
                groupByIndexes.add(i);
            }
            pivotCmd.setGroupByIndexes(groupByIndexes);

            int index = 0;
            String baseName = "ROW_LOCATOR";
            String subGrpMatchAlias = baseName;
            List<String> outputAliases = new ArrayList<String>(groupByAliases.size() + subGroupAliases.size());
            outputAliases.addAll(groupByAliases);
            outputAliases.addAll(subGroupAliases);
            while(outputAliases.contains(subGrpMatchAlias)) {
                subGrpMatchAlias = baseName + "_" + index++;
            }
            pivotCmd.setSubGrpMatchAlias(subGrpMatchAlias); 

            return pivotScript;
        }
    }
